<?php get_header(); ?><?php $the_page=null;$errorpageid=get_option('404pageid',0);if($errorpageid!==0){$errorpageid=(int) $errorpageid;$the_page=get_page($errorpageid);} ?><div id="four-oh-four"><div class="inn-error"><?php if($the_page==NULL||isset($the_page->post_content)&&trim($the_page->post_content=='')): ?><div class="pr-error"><div class="first-column grid-100 mobile-grid-100 tablet-grid-100"><img alt="404"src="<?php echo get_home_url(); ?>/wp-content/uploads/2021/03/astronot.svg"><p>Ooops!</p><p>The page you are looking for seems to be missing or<br>moved to new url.</p><div style="text-align:center"><a href="<?php echo get_home_url(); ?>">Home</a></div></div></div><?php else: ?><?php echo apply_filters('the_content',$the_page->post_content); ?><?php endif; ?></div></div><?php get_footer(); ?>